create table "public"."Survey_Responses" (
    "id" bigint generated by default as identity not null,
    "created_at" timestamp with time zone not null default now(),
    "data_entry" jsonb,
    "survey_id" uuid
);


alter table "public"."Survey_Responses" enable row level security;

create table "public"."Survey_Summaries" (
    "id" bigint generated by default as identity not null,
    "created_at" timestamp with time zone not null default now(),
    "survey_id" uuid,
    "summary_data" jsonb,
    "updated_at" timestamp with time zone default now()
);


alter table "public"."Survey_Summaries" enable row level security;

CREATE UNIQUE INDEX "Survey_Response_pkey" ON public."Survey_Responses" USING btree (id);

CREATE UNIQUE INDEX "Survey_Summaries_pkey" ON public."Survey_Summaries" USING btree (id);

alter table "public"."Survey_Responses" add constraint "Survey_Response_pkey" PRIMARY KEY using index "Survey_Response_pkey";

alter table "public"."Survey_Summaries" add constraint "Survey_Summaries_pkey" PRIMARY KEY using index "Survey_Summaries_pkey";

alter table "public"."Survey_Responses" add constraint "Survey_Response_survey_id_fkey" FOREIGN KEY (survey_id) REFERENCES "Surveys"(id) not valid;

alter table "public"."Survey_Responses" validate constraint "Survey_Response_survey_id_fkey";

alter table "public"."Survey_Summaries" add constraint "Survey_Summaries_survey_id_fkey" FOREIGN KEY (survey_id) REFERENCES "Surveys"(id) not valid;

alter table "public"."Survey_Summaries" validate constraint "Survey_Summaries_survey_id_fkey";

