create policy "Enable all for owner users of the responded survey"
on "public"."Survey_Responses"
as permissive
for all
to public
using ((( SELECT auth.uid() AS uid) = ( SELECT "Surveys".owner
   FROM "Survey_Responses" "Survey_Responses_1",
    "Surveys"
  WHERE ("Surveys".id = "Survey_Responses_1".survey_id))));


create policy "Enable all for owner users of the summarized survey"
on "public"."Survey_Summaries"
as permissive
for select
to public
using ((( SELECT auth.uid() AS uid) = ( SELECT "Surveys".owner
   FROM "Survey_Summaries" "Survey_Summaries_1",
    "Surveys"
  WHERE ("Surveys".id = "Survey_Summaries_1".survey_id))));



